(()=>{const e=document.getElementById("city-input");async function t(e){try{const n=await fetch(`http://api.openweathermap.org/data/2.5/weather?q=${e}&units=metric&APPID=d9523b5c914cab4b5f04bd6fa0ad108a`,{mode:"cors"});let o=await n.json(),i={city:(t=o).name,country:t.sys.country,temp:Math.round(t.main.temp),feelsLike:Math.round(t.main.feels_like),wind:Math.round(t.wind.speed),humidity:t.main.humidity,description:t.weather[0].description,imgUrl:`http://openweathermap.org/img/wn/${t.weather[0].icon}@2x.png`};!function(e){const t=document.querySelector(".city-h2"),n=document.querySelector(".temp"),o=document.querySelector(".feels"),i=document.querySelector(".wind"),c=document.querySelector(".humidity"),r=document.querySelector(".weather-icon");t.textContent=`${e.city}, ${e.country}`,n.textContent=`${e.temp}°C`,o.textContent=`Feels like: ${e.feelsLike}°C`,i.textContent=`Wind: ${e.wind}km/h`,c.textContent=`Humidity: ${e.humidity}%`,r.src=e.imgUrl}(i),console.log(i),console.log(o)}catch(e){console.error(e)}var t}document.getElementById("search-btn").addEventListener("click",(function(){t(e.value)})),e.addEventListener("keydown",(function(n){console.log(n.key),"Enter"===n.key&&t(e.value)})),t("Buenos Aires")})();